import './App.css';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
import {
  MDBTable,
  MDBTableHead,
  MDBTableBody,
  MDBRow,
  MDBCol,
  MDBContainer,
  MDBModalBody,
  MDBBtn,
  MDBBtnGroup,
  MDBPagination,
  MDBPaginationItem,
  MDBPaginationLink
} from "mdb-react-ui-kit";

function App() {
  const [data, setData] = useState([]);
  const [value, setValue] = useState("");
  const [sortValue, setSortValue] = useState("");
  const sortOptions = ["name", "email", "phone", "address", "status"];

  useEffect(() => {
    loadUserData(0,5,0);
  }, []);

  const loadUserData = async (start, end, increment) => {
    return await axios
      .get(`http://localhost:5000/users?_start=${start}&_end=${end}`)
      .then((response) => setData(response.data))
      .catch((err) => console.log(err));
  }

  const handleReset = async (e) => {
    loadUserData(0,5,0);
    setValue("");
    setSortValue("");
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    const url = `http://localhost:5000/users?name=${value}`;
    console.log(url);
    return await axios
      .get(url)
      .then((response) => {
        console.log("inside search..", value + " : " + JSON.stringify(response.data));
        setData(response.data);
        setValue("");
      })
      .catch((err) => console.log(err));
  };

  const updateSortValue = async (e) => {
    let value = e.target.value;
    setSortValue(value);
    let url = `http://localhost:5000/users?_sort=${value}&_order=asc`;
    console.log(url);
    return await axios
      .get(url)
      .then((response) => {
        console.log("inside search..", value + " : " + JSON.stringify(response.data));
        setData(response.data);
      })
      .catch((err) => console.log(err));
  };

  const handleFilter = async(status) => {
    let url = `http://localhost:5000/users?status=${status}`;
    console.log(url);
    return await axios
      .get(url)
      .then((response) => {
        console.log("inside search..", value + " : " + JSON.stringify(response.data));
        setData(response.data);
      })
      .catch((err) => console.log(err));
  };



  return (
    <MDBContainer>
      <form
        style={{
          margin: "auto",
          padding: "15px",
          maxWidth: "400px",
          alignContent: "center"
        }}
        className="d-flex input-group w-auto"
        onSubmit={handleSearch}
      >
        <input
          type="text"
          className='form-control'
          placeholder='search name..'
          value={value}
          onChange={(e) => setValue(e.target.value)}
        />
        <MDBBtn type="submit" color='dark'>Search</MDBBtn>
        <MDBBtn className='mx-2' color="info" onClick={() => handleReset()}>Reset</MDBBtn>
      </form>

      <div style={{ marginTop: "10px" }}>
        <h2 className='text-center'>search, filter, sort and pagination using JSON fake rest API</h2>
        <MDBRow>
          <MDBCol size="12px">
            <MDBTable>
              <MDBTableHead dark>
                <tr>
                  <th>no</th>
                  <th>name</th>
                  <th>email</th>
                  <th>phone</th>
                  <th>address</th>
                  <th>id</th>
                  <th>status</th>
                </tr>
              </MDBTableHead>
              {data.length === 0 ? (
                <MDBModalBody className='align-center mb-0'>
                  <tr>
                    <td colSpan={8} className='text center mb-0'> No data Found!! </td>
                  </tr>
                </MDBModalBody>
              ) : (
                data.map((item, index) =>
                (
                  <MDBTableBody key={index}>
                    <tr>
                      <th scope='row'>{index + 1}</th>
                      <td>{item.name}</td>
                      <td>{item.email}</td>
                      <td>{item.phone}</td>
                      <td>{item.address}</td>
                      <td>{item.id}</td>
                      <td>{item.status}</td>
                    </tr>
                  </MDBTableBody>
                ))
              )};
            </MDBTable>
          </MDBCol>
        </MDBRow>
      </div>
      <MDBRow>
        <MDBCol size="8">
          <h5>Sort By:</h5>
          <select style={{ width: "50%", borderRadius: "2px", height: "35px" }}
            onChange={updateSortValue}
            value={sortValue}>
            <option value="" key="0">Select Select Value..</option>
            {sortOptions.map((item, index) => (
              <option value={item} key={index}>{item}</option>
            ))}
          </select>
        </MDBCol>
        <MDBCol size="4">
          <h5>Filter by Status :</h5>
        <MDBBtnGroup>
          <MDBBtn color='success' onClick={() => handleFilter("Active")}>Active</MDBBtn>
          <MDBBtn color='danger' style={{marginLeft:"2px"}} onClick={() => handleFilter("Inactive")}>Inactive</MDBBtn>
        </MDBBtnGroup>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}

export default App;
