import {
    MDBCol,
    MDBBtn,
    MDBBtnGroup
} from "mdb-react-ui-kit";

export default function Theme({ callUpdateTheme,txt}) {
    function updatTheme(val) {
        callUpdateTheme(val);
    }
    return (
        <MDBCol size="4">
            <MDBBtnGroup>
                <MDBBtn color={txt} onClick={() => updatTheme("Light")}>{txt == "Light"? "Dark":"Light"}</MDBBtn>
                {/* <MDBBtn color='dark' style={{ marginLeft: "2px", marginRight: "2px" }} onClick={() => updatTheme("Dark")}>Dark</MDBBtn>*/}
            </MDBBtnGroup>
        </MDBCol>
    )
}
