import './App.css';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { MDBTable, MDBTableHead, MDBTableBody, MDBRow, MDBCol, MDBContainer, MDBModalBody } from "mdb-react-ui-kit";

function App() {
  const [data, setData] = useState([]);


  useEffect(() => {
    loadUserData();
  }, []);

  const loadUserData = async () => {
    return await axios
      .get("http://localhost:5000/users")
      .then((response) => setData(response.data))
      .catch((err) => console.log(err));
  }

  console.log("Data" + data);
  return (
    <MDBContainer>
      <div style={{ marginTop: "100px" }}>
        <h2>search, filter, sort and pagination using JSON fake rest API</h2>
        <MDBRow>
          <MDBCol size="12px">
            <MDBTable>
              <MDBTableHead dark>
                <tr>
                  <th>NAME</th>
                  <th>EMAIL</th>
                  <th>PHONE</th>
                  <th>ADDRESS</th>
                  <th>ID</th>
                  <th>STATUS</th>
                </tr>
              </MDBTableHead>
              {data.length === 0 ? (
                <MDBModalBody className='align-center mb-0'>
                  <tr>
                    <td colSpan={8} className='text center mb-0'> No data Found!! </td>
                  </tr>
                </MDBModalBody>
              ) : (
                data.map((item, index) =>
                (
                  <MDBTableBody key={index}>
                    <tr>
                      <th scope='row'>{index + 1}</th>
                      <td>item.NAME</td>
                      <td>item.NAME</td>
                      <td>item.NAME</td>
                      <td>item.NAME</td>
                      <td>item.NAME</td>
                    </tr>
                  </MDBTableBody>
                ))
              )};
            </MDBTable>
          </MDBCol>
        </MDBRow>
      </div>
    </MDBContainer>
  );
}

export default App;
