import React from 'react';
import { useState } from 'react';
import Header from './components/Header';
import Players from './components/Players';
import GameBoard from './components/GameBoard';
import GameLogs from './components/GameLogs';
import { WINNING_COMBINATIONS } from './components/winning-combinations';

function App() {
  const [gameturns, setGameTurns] = useState([]);
  const [activePlayer, setActivePlayer] = useState("X");

  function updateActivePlayer(rowIndex, colIndex) {
    setActivePlayer((currActPlys) => currActPlys === "X" ? "O" : "X");
    setGameTurns(
      prevTurn => {
        let currentActPlayer = "X";
        if (prevTurn.length > 0 && prevTurn[0].player === "X") {
          currentActPlayer = "O";
        }
        const updatedTurns = [
          { square: {row: rowIndex, col:colIndex }, player: currentActPlayer },
          ...prevTurn,
        ];
        return updatedTurns;
      });
  }

  return (
    <main>
      <Header></Header>
      <div id="game-container">
        <ol id="players" className="highlight-player">
          <Players initialName="Player 1" Symbol="X" isActive={activePlayer === "X"}></Players>
          <Players initialName="Player 2" Symbol="O" isActive={activePlayer === "O"}></Players>
        </ol>
        <GameBoard onSelectSquare={updateActivePlayer} gameturns={gameturns}></GameBoard>
      </div>
      <GameLogs gameturns={gameturns}></GameLogs>
    </main>

  );
}

export default App;
