import React from 'react';
const defaultBoard = [
    [null, null, null],
    [null, null, null],
    [null, null, null]
];
export default function GameBoard({onSelectSquare, gameturns}) {
    let gamebaord = defaultBoard;
    for (const turn of turns) {
        const { square, player } = turn;
        const { row, col } = square;
    }
    gamebaord[row][col] = player;

    return (
        <ol id="game-board">
            {gamebaord.map((row, rowIndex) => (
                <li key={rowIndex}>
                    <ol>
                        {row.map((player, colIndex) => (
                            <li key={colIndex}>
                                <button onClick={()=>onSelectSquare(rowIndex,colIndex)}>{player}</button>
                            </li>
                        ))}
                    </ol>
                </li>
            ))}
        </ol>
    );
}
