import { useState } from 'react';
import React from 'react';

export default function GameBoard({onSelectSquare,activePlayerSymbol}) {
    const defaultBoard = [
        [null, null, null],
        [null, null, null],
        [null, null, null]
    ];
    const [gamebaord, updateGameBoard] = useState(defaultBoard);

    function handleUserClick(rowIndex, colIndex) {
        updateGameBoard((prevGameBoard) => {
            const updatedBoard = [...prevGameBoard.map(innerArry => [...innerArry])];
            updatedBoard[rowIndex][colIndex] = activePlayerSymbol;
            return updatedBoard;
        });
        onSelectSquare();
    }


    return (
        <ol id="game-board">
            {gamebaord.map((row, rowIndex) => (
                <li key={rowIndex}>
                    <ol>
                        {row.map((player, colIndex) => (
                            <li key={colIndex}>
                                <button onClick={()=>{handleUserClick(rowIndex,colIndex)}}>{player}</button>
                            </li>
                        ))}
                    </ol>
                </li>
            ))}
        </ol>
    );
}
