import { useState } from 'react';
import React from 'react';

export default function Players({ initialName, Symbol }) {
    const [playerName, setPlayerName] = useState({ initialName });
    const [isEditing, setEditing] = useState(false);

    function handleEditing() {
        setEditing((editing) => !editing);
    }
    const playerNameEdited = <span className='player-name'>{initialName}</span>;
    if(isEditing){
        playerNameEdited = <input type="text" required />; 
    } 

    function handleOnChange(cntx) {
        setPlayerName("");
    }

    return (
        <li>     
            <span className='player'>
                {playerNameEdited}
                <span className='player-symbol'>{Symbol}</span>
            </span>
            <button onClick={handleEditing} onChange={handleOnChange}>Edit</button>
        </li>
    );
}
